function retiraValueAcessi(a){$(a).val("")}$(function(){$(".retiraValue").click(function(){retiraValueAcessi(this)});$(".retiraValue").focus(function(){retiraValueAcessi(this)})});function seleciona_busca(){$(".botao_busca").show();$("#box_busca label.styleCheckbox").show();$("#busca").css("background","#FFF")}$(function(){$("#busca").click(function(){seleciona_busca()});$("#busca").focus(function(){seleciona_busca()})});function createCookie(c,d,e){if(e){var b=new Date();b.setTime(b.getTime()+(e*24*60*60*1000));var a="; expires="+b.toGMTString()}else{a=""}document.cookie=c+"="+escape(d)+a+"; path=/;"}function readCookie(b){var e=b+"=";var a=document.cookie.split(";");for(var d=0;d<a.length;d++){var f=a[d];while(f.charAt(0)==" "){f=f.substring(1,f.length)}if(f.indexOf(e)==0){return unescape(f.substring(e.length,f.length))}}return null}function controleAccess(){var a=readCookie("filtro");if(a!=null){contraste(escape(a))}}function contraste(a){if(a==1){$("body").addClass("filtro_Preto_Branco");$("body").removeClass("filtro_Invertido");createCookie("filtro",1,30)}else{if(a==2){$("body").addClass("filtro_Invertido");$("body").removeClass("filtro_Preto_Branco");createCookie("filtro",2,30)}else{$("body").removeClass("filtro_Preto_Branco");$("body").removeClass("filtro_Invertido");createCookie("filtro",0,30)}}};